style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(http://), url-prefix(https://), url-prefix(about:blank), url-prefix(file://) {

*	{color: black !important;	
	background: white !important;}

button, input[type="file"], input[type="submit"], input[type="button"], input[type="reset"]
	{color: black !important;}

a[href]
	{color: #1a5df4 !important;}

@media screen, projection, handheld, tv
	{ input, textarea, select
		{color: black !important;}}

}

.tabbrowser-tab[selected="true"]{ background: white; color: #222 !important;}
.tabbrowser-tab[selected="true"]:hover{ background: white; color: #222 !important;}
.tabbrowser-tab[selected="false"]{ background: white;}
.tabbrowser-tab:not([selected="true"]):hover { background: white; color: #222 !important;}
.tabbrowser-tab:not([selected="true"]){ background: white; color: #222 !important;}
.tabbrowser-tab { background: white !important;}
