style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("pen.io"),
url-prefix(javascript:) {
   
    /* applies font to all generic HTML tags */
    *   {
        font-family: Tahoma, Sans !important;
        font-size: 10pt !important;
        text-decoration: none !important;
        line-height: 20px;
        direction:rtl;
        text-align: right;
    }
        .page h1 {
        font-family: arial, Sans !important;
        font-size: 30pt !important;
        letter-spacing: 0px;
        font-style: normal !important;
        font-weight: bold !important;  
        }
        #pagecontent {
        word-spacing: 0px !important;
        }
        #pagecontent h1, #pagecontent h2, #pagecontent h3, #pagecontent h4 {
        direction:rtl;
        text-align:right !important;
        }
}
