style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iranamerica.com") {

* { font-family: "XB Yas", Yas, Tahoma !important; }

body {
    font: 10pt/150% "XB Yas", Yas,tahoma !important;
}

td, th, p, li {
    font: 14pt/200% "XB Yas", Yas,tahoma !important;
    line-height: 16pt;
}

font {
    font: 14pt/200% "XB Yas", Yas,tahoma !important;
}

.tcat {
    font: bold 13pt/150% "XB Yas", Yas,tahoma !important;
}

.thead {
    font: bold 11pt/150% "XB Yas", Yas,tahoma,tahoma !important;
}

.button {
    font: 12pt/160% "XB Yas", Yas,tahoma !important;
}

select {
    font: bold 12pt/160% "XB Yas", Yas, tahoma !important;
    height:28px;
}

.navbar {
    font: bold 14pt/160% "XB Yas", Yas,arial !important;
}

.vbmenu_control {
    font: bold 16px/160% "XB Yas", Yas,tahoma,tahoma !important;
    padding: 3px 6px;
}

.fieldset, .fieldset td, .fieldset p, .fieldset li {
    font-size: 14pt !important;
}

.title {
    font: Bold 13pt/135% "XB Yas", Yas, Tahoma !important;
}

.text {
    font: Bold 12pt/135% "XB Yas", Yas, tahoma !important;
}

.alt1 a:link, .alt2_alink, .alt2Active a:link, .alt2Active_alink {
    font-size: 12pt ;
}

.alt2 a:link, .alt2_alink, .alt2Active a:link, .alt2Active_alink {
    font-size: 12pt;
}

option, optgroup {
    font-family: "XB Yas", Yas, tahoma !important;
    font-size: 12pt !important;
}

.bigusername {
    font-size: 20pt !important;
    line-height:30pt !important;
}

.lastposts_toolbar {
    font-size: 10pt !important
}

.smallfont {
    font: 11pt/160% "XB Yas", Yas, tahoma !important;
}

.wysiwyg {
    font: 13pt/180% "XB Yas", Yas, tahoma !important;
}

textarea, .bginput {
    font-size:12pt;
}

}
