style.css:
*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var ) {
	font-family: "Microsoft Yahei" !important;
	text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}

pre, code, kbd, samp, var {
    font-family: "Monaco", "Consolas", "Microsoft Yahei" !important;
	text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}

@-moz-document domain("douban.com") {
*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var ) {
	font-family: "Microsoft Yahei", ilmenicons !important;
	text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}
}

@-moz-document domain("tmall.com") {
*:not([class*="icon"]):not(i):not(pre):not(code):not(kbd):not(samp):not(var ) {
	font-family: tm-detail-font, tmall3c, "Microsoft Yahei" !important;
	text-shadow: 0.05em 0.05em 0.2em #999999 !important;
}
}
