style.css:
@-moz-document domain("botva.ru"), domain("botva.mail.ru") {
.post_image {
  display:none;
}
.msg_main {
  min-height: 0 !important; 
}
.msg_text {
  min-height: 0 !important; 
  width: 515px !important;
}
.msg_main .msg_title {
  margin-left: 85px !important;
  padding-top: 8px !important;
}
.post_chb {
  padding-top:8px !important;
  height: 0 !important;
}
.msg_main .msg_date {
  top: 10px;
  left: 20px;
  color: #542F1B !important;
  font-size: 11px !important;
  font-weight: bold;
  width: 85px;
  height: 15px;
}
div[id^="post_div_"].p_row_2, div[id^="post_div_"].p_row_1{
  min-height: 0 !important; 
}
}
