style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newser.cc") {

#head,#main{
width:880px !important;
margin:0 auto !important;
padding:0 !important;
border:0 !important;
}

#contents{
margin:0 !important;
padding:0 !important;
background:#fff !important;
width:880px !important;
}

#newsIn{
margin:0 !important;
padding:0 10px !important;
width:100% !important;
}

#newsCont .content{
padding-left:10px !important;
}

.news{
margin:0 !important;
}

.news > div:last-child{
margin-bottom:10px !important;
}

div#hmenu ul{
padding:0 !important;
}

#hmenu ul li{
width:80px !important;
border-left:#966 1px solid !important;
border-top:#966 1px solid !important;
border-right:#633 1px solid !important;
border-bottom:#633 1px solid !important;
}

#newsIn > div:first-child,
#hmenu ul li:first-child,
.content > div:last-child,
#hbody li.menu,
#rightCont,
.genre11,
.genre11 + div,
.genre11 + div + div,
.nl > div:last-child,
.content + div,
#newsIn > table{
 display:none !important;
}

}
