style.css:
@-moz-document domain("facebook.com") {
/* Facebook Revision Style
   Removes adds from the different pages.
*/


/* Remove adds from the timeline */

#rightCol {
  display: none;
}

/* Remove adds from the image viewer */

#fbPhotoSnowliftAdsSide {
  display: none;
}

/* Remove adds from the messages screen */

#MessagingNetegoWrapper {
  display: none;
}


/* Let's expand a bit the main content */

#mainContainer #contentArea, #MessagingMainContent {
  width: 95%;
}

.uiUfi, .uiStream .shareRedesign {
  width: 100%;
}

.MessagingMessage .content {
  width: 100% !important;
}
}
