style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

#blueBar {
    background-color: #38c238 !important;
    border-bottom: 1px solid #38c238 !important;
    min-width: 981px;
    position: relative;
    z-index: 300;
}

#blueBar a {
    color:#157e00 !important;
    background-color: #38c238 !important;
}

#blueBar a:hover {
    color:#ffffff !important;
    background-color:#00a920 !important;
}

#userNavigation {
    background-color: #38c238 !important;
}

#userNavigation a:hover{
    color:#ffffff !important;
    background-color:#00a920 !important;
}

#pageNav .menuDivider {
    background-color: #00a920 !important;
    font-size: 0;
    height: 1px;
    line-height: 0;
    margin: 6px 7px;
}

}
