style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("review.cyanogenmod.com") {

a, a:visited, a:hover,
#gerrit_body:not(.patch) tr:nth-child(2n) {
    color: #000 !important;
}

#gerrit_body:not(.patch) td {
    border: 1px solid #C5E2EA !important;
}

#gerrit_body:not(.patch) > div > div > div > table td,
#gerrit_body:not(.patch) > div > div > div > div > table:last-child td,
#gerrit_body:not(.patch) > div > div > div > div > table td[align=left],
#gerrit_body:not(.patch) > div > div > div > div > table:nth-child(4) td,
#gerrit_body:not(.patch) > div > div > div > div > table:nth-child(5) td,
#gerrit_body:not(.patch) a.header > table td {
    border: 0 !important;
}

}
