style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stosstrupp-gold.eu") {
    #bbpress-forums ul.bbp-replies {
        background-color: #222;
    }
    
    #bbpress-forums li.bbp-header, #bbpress-forums li.bbp-footer {
        background-color: transparent !important;
    }
    
    #bbpress-forums div.bbp-topic-header, #bbpress-forums div.bbp-reply-header {
        background-color: transparent !important;
    }
    
    #bbpress-forums div.odd, #bbpress-forums ul.odd {
        background-color: transparent !important;
    }    
    
    #bbpress-forums div.even, #bbpress-forums ul.even {
        background-color: #111 !important;
    }
    
    #bbpress-forums div.bbp-reply-header {
        background-color: #181818 !important;
        border-bottom: 1px dotted #555 !important;
    }
    
    #bbpress-forums div.bbp-reply-form div.wp-editor-wrap iframe,
    #bbpress-forums div.bbp-topic-form div.wp-editor-wrap iframe{
        background-color: #f0f0f0 !important;    
    }
}
