style.css:
@-moz-document domain("vk.com") {
	/* Profile info >change */
	#profile_full_info h4 b, #profile_full_info h4 a.fl_r, #profile_full_info h4 a.fl_l {
		color: black !important
		}
	.profile_no_info {
		display: none !important
		}
	#profile_info .profile_info {
		padding-bottom: 0px !important
		}
	/* Header line */
	#header {
		background: #E1E7ED !important; 
		border: 1px solid #E1E7ED !important
		}
	
	/* TOP */
	#page_header, #pageHeader {
		background: url(http://imgs.su/tmp/2012-12-09/1355049481-593.jpg) !important
		}
	#top_logo_down {
	    background: url(http://imgs.su/tmp/2012-12-09/1355050425-593.jpg) !important
		}
	#page_header .back, #page_header .right, #page_header .left, #pageHeader .header_back,
	#pageHeader .header_right, #pageHeader .header_left {
		display:none !important
		}
}
