style.css:
@namespace url(http://www.w3.org/1999/xhtml);
 @-moz-document domain("twitter.com") {
 body { font-size:65% !important; }
 #currently { display:none !important; }
 #header { display:none !important; }
 div.content-bubble-arrow { background:none !important; }
 #content .doing .thumb { display:none !important; }
 #content #doingForm .bar h3 label { display:none !important; }
 #content #doingForm .bar span { position:static !important;  }
 #content #doingForm .info textarea { margin-top:0.8em !important;  }
 /* #navigation { display:none !important; } */
 #side { margin-top:19px !important; border-top: 1px solid #bbbbbb !important; }
 #side_ad_base { display:none !important; }
 a span.label { display:none !important; }
 #side div.user_icon img { padding-right:1.8em !important; }
 #side #me_name { display:none !important; }
}
