style.css:
@-moz-document domain("do.com") {
* {
font-family:Tahoma, Verdana!important;
font-size:8pt!important;
}

.comment-text,.name,.text-link,.description {
direction:rtl!important;
text-align:right!important;
line-height:12pt!important;
}

.detail ul.association li .delete,.comments .delete {
left:40px!important;
}

.drag-handle,.checkbox,.avatar-wrapper-32 {
float:right!important;
}

.wysihtml5-sandbox body {
text-align:right!important;
}

#header .name {
line-height:35pt!important;
}

.list-body .task .info-block .counter,.list-body .task .info-block .inline-indicator {
float:left!important;
}
}
