style.css:
@-moz-document domain("twitter.com") {
p.tweet-text-rtl,
p[lang="fa"],
p.bio-description-rtl,
strong.fullname-rtl,
#tweet-box-mini-home-profile[dir="rtl"],
.tweet-box[dir="rtl"],
.u-dir[dir="rtl"],
.compose-text[dir="rtl"]
{ 
    font-family: Tahoma, "Iranian Sans", serif !important; 
	/** font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif; **/
}

strong.fullname-rtl
{ font-size:0.78em; }

[lang="fa"] a[rel="hashtag"] { direction: rtl; }

.twitter-timeline-link.u-hidden { display: inline !important; }
}
