style.css:
@-moz-document domain("todoist.com") {

body { background: #B3B3B3 !important;}
.left_menu { background: #B3B3B3 !important;}
#content { background: #D3D2D2 !important;}
li.current table { background: #B3B3B3 !important;}
li.clickable table tr:hover { background: #B3B3B3 !important;}
li.current table .name {font-size: 16px !important;}
.items .name {font-size: 14px !important;}
#top_bar,.input_q {background: #D3D2D2 !important;}
.counter_count {color: #90211D !important;}
#editor .input_due_date {background: #D3D2D2 !important;}
.form_content table {border:0 !important;}
#all_holder {max-width: 1150px !important;margin-left: 300px !important;}
#editor {width: 830px !important;}
#top_filters li.filter:hover {background: #B3B3B3 !important;}
#top_filters .filter.current {background: #B3B3B3 !important;font-size: 16px !important;color: #000 !important;}
#td_logo .invisible_bg {background: #B3B3B3 !important;}
#td_logo {background: #B3B3B3 !important;}
.project_item {color: #666 !important;}
#agenda_view .no_tasks, #agenda_view .no_tasks li, #agenda_view .no_tasks .date, #agenda_view .no_tasks a {color: #888 !important;}
.h2_date {color: #7E6868 !important;}
}
