style.css:
@-moz-document domain("alfalfalfa.com") {

  #blogcontainer, #left, div.container_r, div.fullbody, div.menu, div.mv,
  div[class*=posted], div.pager, div.social_buttons, div.fix_ent_ad_button,
  div.fix_10x10_md1, div.fix_related, *[id*="ads"], #article_footer_amazon,
  div.Hleft, #fixfooter, div.trackbackurltop, div.fb_iframe_widget,
  div.fb-activity+br+br+div.rr, div.takecommenthead, div.Hleft2,
  #zenback, br[clear="all"], div.trackbackhead, div.google-user-ad,
  div.fix_microad_d, #zenback_loady
  { display: none !important; }

  .r1, .r2, .rr, .mm {
    margin: 5px 0 1em 20px !important;
    font-size: 16px !important;
    padding-right: 20px !important;
    line-height: 1.4 !important;
  }
  .rr, .mm {
    color: #444 !important;
  }

  div.sidebody, .hrr+.rr {
    font-size: 12px !important;
    margin: 0 0 0 5px !important;
  }
  #wrapper {
    float: none !important;
    width: 790px !important;
    margin: 2em auto 0 !important;
  }
  .container_l {
    float: none !important;
  }
  body {
    background-color: #eee !important;
  }
  .blogbody {
    background-color: #f9f9f4 !important;
    border: none !important;
    box-shadow: 0 0 4px #ddd;
    border-radius: 4px;
    padding: 2em 2em 0 !important;
    margin-bottom: 2em !important;
  }
  #r_post {
    width: 600px !important;
  }

}
