style.css:
@-moz-document domain(www.wikipedia.org) {

    div#simpleSearch {
        display: block;
        position: relative;
        width: 14em;
        height: 1.4em;
        margin-top: 0.65em;
        min-height: 1px;
    }

    div#simpleSearch input#searchInput {
        position: absolute;
        top: 0;
        left: 0;
        width: 90%;
        margin: 0;
        padding: 0;
        padding-top: 0.2em;
        padding-bottom: 0.2em;
        padding-left: 0.2em;
    }

    div#simpleSearch button#searchButton {
        position: absolute;
        top: 0;
        right: 0;
        width: 10%;
        margin: 0;
        padding: 0;
        padding-top: 0.3em;
        padding-right: 0.4em;
        padding-bottom: 0.2em;
    }

    div#simpleSearch button#searchButton img {
        margin: 0;
        margin-top: -3px;
        padding: 0;
    }

    div#simpleSearch button#searchButton > img {
        margin: 0;
    }



}
