style.css:
@-moz-document url-prefix("http://www.neoteo") {
body {
background: #040808 !important;
}

li, a {
color: #1ABC9C !important;
}

header, footer, .sidebar_bg, .post, .post_inner, .entry-content, .comment-entry, #copyright  {
background: #001813 !important;
color: #808080 !important;
}

div.postmeta, div.zensell-external-link, div.authorblock {
background: transparent !important;
color: #1ABC9C !important;
}

.format-video .zoom {
background: rgba(26, 188, 156, 0.25) !important;
}

.fancybox-overlay {
background: rgba(4, 8, 8, 0.93) !important;
}
}
