style.css:
@-moz-document
	 url-prefix('http://userstyles.org/styles/'),
	 url-prefix('https://userstyles.org/styles/')
{
  DIV#hidden-meta {
    display: inline-block;
    border: thin solid magenta;
  }
  #hidden-meta > *:before {content: attr(id) ": ";}
  #hidden-meta > DIV {display: block}
}
