/*__ Noise _________________________________________________________________________________________*/

.black-bg {
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

.black-bg:hover {
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Noise Red _____________________________________________________________________________________*/

.headerBG {
	background-image: linear-gradient(to bottom, rgba(255, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

.headerBG:hover {
	background-image: linear-gradient(to bottom, rgba(255, 0, 0, .3), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}

/*__ Red Gradient _________________________________________________________________________________________*/

.search-holder .suggest ul li:hover .suggestAnchor, .search-holder .suggest ul li .hovered,
.attentionBox {
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
}

