style.css:
@-moz-document domain("ehow.com") {
 ::-webkit-scrollbar {
 	/* Width */
 	width: 1.2em!important;
 }

 /* Color of handle ___________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track ______________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom ___________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }
}

@-moz-document domain("ehow.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

.component-article-template-ls .article .component-article-byline .writer-info .writer-name-article-date,
.home-view .homepage-section-title
 {
	text-transform: capitalize!important;
}

.component-article-byline .writer-info .writer-name-article-date .writer-name span.writer-profile-link,
.component-article-template-ls .PopularSearches,
.site-header .inner .menu-overlay .menu-overlay__category-links,
.view .article .component-article-sections .component-article-section-things-needed .things-needed-list,
:focus
 {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

.component-article-template-ls .article .component-article-byline .writer-info .writer-name-article-date,
.home-view .homepage-section-title,
.home-view .section--hero-home .hero-home-wrapper .title,
.view .article .component-article-inline-slideshow .slideshow-container .slideshow__slide-container .slideshow__slides .slide-container .text-slide .slideshow__content
{
    font-family: sans-serif;
}

.component-article-byline .writer-info .writer-name-article-date .writer-name span.writer-profile-link,
.component-article-template-ls .article .component-article-byline .writer-info .writer-name-article-date
 {
	font-size: 21px!important;
}

.view .article .component-article-inline-slideshow .slideshow-container .slideshow__slide-container .slideshow__slides .slide-container .text-slide .slideshow__content,
body
 {
	font-size: 19px!important;
}

selector
 {
	font-size: 17px!important;
}

.view .component-breadcrumbs .breadcrumbs .breadcrumbs__item
 {
	font-size: 15px!important;
}

selector
 {
	line-height: 19px!important;
}

/*___ Remove Items _________________________________________________________________*/

.ad,
.component-ad-adsense,
.component-ad-link,
.component-article-jwplayer .jwplayer__container-outer .jwplayer__container.jwplayer__container--sticky,
.component-article-references,
.component-article-section-ad
 {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.component-article-template-ls .header-lower-inner .social-share-icons-wrapper.is-sticky {
    left: calc(32% - 549px);
}

.component-article-template-ls .header-lower-inner .social-share-icons-wrapper {
    left: calc(19% - 380px);
}

.component-article-template-ls .right-rail {
    right: -118px;
}

.component-article-template-ls .right-rail.is-fixed {
    right: calc(31% - 485px);
}

/*___ Margins ______________________________________________________________________*/

.home-inner.home-inner--padding
 {
	margin: 12px!important;
}

selector
 {
	margin: 3px 6px!important;
}

/*___ Paddings _____________________________________________________________________*/

selector
 {
	padding: 12px!important;
}

selector
 {
	padding: 3px 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector
 {
	max-height: none!important;
}

selector
 {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

.component-article-inline-slideshow,
.component-article-template-ls .article-rail-container,
.component-article-template-ls .article-wrapper,
.container,
.site-main .view,
.view-article .article-container
 {
	max-width: none!important;
}

selector
 {
	width: auto!important;
}

.container,
.site-main .view,
.view-article .article-container
 {
	width: 98%!important;
}

.component-article-inline-slideshow,
.component-article-template-ls .article-rail-container
 {
	width: 80%!important;
}









}

@-moz-document domain("ehow.com") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.view .component-article-template-ls .component-article-rcp .related-content .related-content__item .item__section.item__section--title a h3:hover,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.component-article-inline-slideshow .slideshow-container .slideshow__header-container .slideshow__count,
.view .article .component-article-sections .component-article-section-image .article-image__caption,
.view .component-article-template-ls .component-article-rcp .related-content .related-content__item .item__section.item__section--title a h3,
li,
p
 {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.component-card-article,
.home-view .section--hero-home .hero-home-wrapper .title,
h1,
h2
 {
	color: #F0E890!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.component-article-byline .writer-info .writer-name-article-date .writer-name span.writer-profile-link,
.component-article-inline-slideshow .slideshow-container .slideshow__header-container .slideshow__title-wrapper,
.view .component-article-byline-old .writer-info .writer-name-article-date,
h3,
h4
 {
	color: #e0b880!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/


h5,
h6,
label
 {
	color: #F0A060!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/


.article-date,
time
 {
	color: #07beff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


selector
 {
	color: #07ff24!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/


selector
 {
	color: #ff0707!important;
	
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
	padding-left: 6px!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
