pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
  color: #f48f8f;
  opacity: 1; 
}























}

@-moz-document domain("ehow.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

selector {
	background-image: none;
}

#app .component-modal-newsletter .popup-mask {
    background-color: rgba(0, 0, 0, 0.94);
}

.view .article .component-article-inline-slideshow .slideshow-container .slideshow__slide-container .slideshow__slides .slide-container .final-slide,
.view .article .component-article-inline-slideshow .slideshow-container .slideshow__slide-container .slideshow__slides .slide-container .text-slide {
    background-color: rgba(0, 0, 0, 0.86);
}


/*___ Background Gray ______________________________________________________________*/

.default-component--article-legacy .site-footer, .default-component--article .site-footer,
.site-header
 {
	background-color: #060505!important;
}

selector
 {
    background-color: #0a0909!important;
}

/*___ Background 0000 ______________________________________________________________*/


.component-card-article .component-card-article-image .image-wrapper
 {
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

.view.view-category,
#app,
.component-article-inline-slideshow .slideshow-container .slideshow__slide-container .slideshow__slides .slide-container .slide-wrapper,
.component-article-section-image .image-container .image-wrapper,
.home-view,
