.newsletter-form-wrapper .newsletter-form .form .form-row .form-row__submit,
.site-header .inner .menu-items .menu-items__item.menu-items__item--search-form .search-form .submit-button,
.view .article .component-article-inline-slideshow .slideshow-container .slideshow__arrow
 {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.component-article-inline-slideshow .slideshow-container .slideshow__arrow-top:hover,
.newsletter-form-wrapper .newsletter-form .form .form-row .form-row__submit:hover,
.site-header .inner .menu-items .menu-items__item.menu-items__item--search-form .search-form .submit-button:hover,
.view .article .component-article-inline-slideshow .slideshow-container .slideshow__arrow:hover
 {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

.component-article-inline-slideshow .slideshow-container .slideshow__arrow-top:active,
.newsletter-form-wrapper .newsletter-form .form .form-row .form-row__submit:active,
.site-header .inner .menu-items .menu-items__item.menu-items__item--search-form .search-form .submit-button:active,
.view .article .component-article-inline-slideshow .slideshow-container .slideshow__arrow:active
 {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

#app .component-modal-newsletter .email-capture-popup-container,
.container,
.home-inner.home-inner--padding,
.view-article .article-container
 {
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
