style.css:
@-moz-document url-prefix("http://universarium.org") {

.user-data .user-avatar a,
.block-course-top .lector-avatar,
.comments-wrapper .user-avatar
{ border-radius: 0 !important; }

/* Course page */
.block-bg { padding: 20px 0 !important; }
.comments-wrapper { padding: 10px 0 0 !important; }
.comments-wrapper .comment { padding-top: 10px !important; padding-bottom: 10px !important; }

/* Home page */
.block-main-page-top .frontpage-block,
.block-main-page-top .course .inner,
.block-main-page-top .course a { height: 150px !important; }
.block-main-page-top .course-description h4 { font-size: 20px !important; }
.block-main-page-top .feedback-block .block-description .icon { display: none !important; }
.course.frontpage-block.course-common.span4,
.course.frontpage-block.course-wide.span8 { width: 50% !important; }

}
