style.css:
@-moz-document domain("wikipedia.org") {
div#mw-panel div.portal div.body ul li a, a, div.vectorTabs li a {
color: #AD068C;
}


div#mw-panel div.portal div.body ul li a:visited, a:visited {
color: #800076;
}


#mw-panel, body, #mw-head {
  background: rgb(255, 203, 253);
}

div#content, div.vectorTabs li, div#content, div.vectorTabs {
  background: rgb(255, 226, 253)!important;
  list-style-type: none;
}


#p-logo a {
  background: url('http://static4.wikia.nocookie.net/__cb20120310140358/gintama/pl/images/f/f0/600px-Female-gender-sign.png') center center no-repeat!important;
  background-size: contain!important;
}

}
