css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://(www.)?torrentsm((d.(com|eu|me))|(oldova.(com|org|net))).*") {
div,form * {
    background: transparent ;
}

table, fullWidth, td, form, img, .message {
    color: #FFF ;
}

#torrents > table > tbody > tr  {
    background-color:transparent !important;
}

body, html, table, textarea {
    background: rgba(0,0,0,.05)url(http://www.designbolts.com/wp-content/uploads/2012/11/Dark-Geometric-Semi-Dark-Seamleass-Pattern-for-website-backgrounds.jpg) left center fixed repeat ;
    background-size: 100%;
}

*,a,h2,h3,h1,div,a.div {
    color: #3399ff ;
}

a:hover,h2:hover,h3:hover,h1:hover {
    color: #3333ff;
}

td#activateTimerText, td#watcherText, td.comment.inited {
    color: green ;
    text-shadow: #000 1px 1px 4px ;
    background-color: rgba(0,0,0,.8) ;
}

#menutable img {
    height: 1px;
}

div#top_menu>table>tbody>tr>td:first-child>a {
    display: block ;
    background: url("http://funkyimg.com/i/2vobS.png") no-repeat scroll 0 0 
transparent ;
    width: 280px ;
    height: 76px ;
}

#tbl_menu a {
    color: #fff ;
    padding: 0 5px ;
    height: 26px ;
    line-height: 26px ;
    display: inline-block ;
}

#tbl_menu a:hover {
    background: #4370A0 ;
    border: 1px solid #335477 ;
}

#tmd_logo {
    display: none ;
}

p.center a {
    box-shadow: rgba(255, 255, 255, 0.0980392) 0px 0px 0px 1px inset;
    color: rgb(112, 204, 255);
    display: inline-block;
    height: 22px;
    min-width: 15px;
    text-align: center;
    text-decoration: none solid rgb(112, 204, 255);
    text-shadow: rgba(0, 161, 250, 0.4) 0px 0px 5px;
    background: rgba(255, 255, 255, 0.0392157) none repeat scroll 0% 0% / auto padding-box border-box;
    border: 0px none rgb(112, 204, 255);
    border-radius: 3px 3px 3px 3px;
    font: normal normal normal 13px/22px UbuntuRegular;
    outline: rgb(112, 204, 255) none 0px;
    overflow: hidden;
    margin-top: 0px;
    margin-right: 0px;
    margin-bottom: 0px;
    margin-left: 3px;
    padding-top: 0px;
    padding-right: 3px;
    padding-bottom: 0px;
    padding-left: 3px;
}

#user_box a{
    color: #fff !important;
}
}
