@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kingdomofloathing.com"), domain(kol.coldfront.net) {
body {background-color: #ee9898 !important; color:#dc5858 !important;}
img {opacity: 0.75 !important;}
input, select, textarea {	border: 2px white solid !important;
	font-family: Arial, Helvetica, sans-serif !important;
	font-size: 10pt !important;
border-color: #ffaaaa !important ;
color: #ffaaaa !important;
	background-color: #dc8585 !important;}
td[bgcolor="blue"] {background-color: #ee9898 !important; background-image:url(http://www.holger-hennes.de/body_bg.gif) !important; color:#dc5858 !important; border: 1px solid #dc5858 !important;}
td[style*="border: 1px solid"], div[style*="border: 1px solid"], [style*="border: 1px solid"]
 { border-color:#dc5858 !important;}
a:link {	color: #dc5858 !important; }
a:visited {	color: #dc5858 !important; }
a:active {	color: #dc5858 !important; }
td[bgcolor="black"] {background-color: #dc5858 !important;}
td[bgcolor="white"] {background-color: #ee9898 !important;}
font[color="blue"] {color: #884040 !important;}
font[color="#333333"] {color: #884040 !important;}
span[class="black"] {color: #dc5858 !important;}
#ChatWindow font[color="green"] {color: #884040 !important;}
}
