css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.flowdock.com") {
    li.tab {
        display: block;
        min-width: 200px;
    }
    
    ul#tab-list {
        width: 200px;
        margin-left: 0px;
    }
    
    nav#tab-bar.desktop {
        width: 200px;
        height: 100%;
    }
    
    div#user-menu-toggle {
        width: 160px;
    }
    
    div.tab-fade-wrapper:after{
        width: 0px;
    }
    
    div.flow {
        padding-top: 0px;
        padding-left: 200px;
    }

}
