css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("exsite.pl") {
    html, .boxed {background-color:black !important;}
    p, label, div.opis > div[id^="news-id-"] {color: grey !important;}
    a {color: #52527A !important;}
    a:hover {color: gold !important;}
    .featured li:hover {background-color: black !important; border: 2px solid gold !important;}
    div.quote {background-color: black !important; border: 1px solid gold !important; color: grey !important;}
    span.copy > em, div[id^="comm-id-"] > div > b, div[id^="comm-id-"] > b {color:grey !important;}
    h3 > strong > a {color: gold !important;}
    div.scriptcode, div[id^="sp"], textarea#comments, input#sec_code {background-color: black !important; color:grey !important;}
    a.recat {color:white !important; opacity: 0.69 !important;}
     
    aside> div:last-child, div#ad, div#adkon, div[class^="reklama"], div[id^="reklama"], div#billboard, div[id^="afbd"] {display:none !important;}
    @keyframes blink { 50% { border-color: #ff0000; }} 
    input#sec_code {animation: blink .5s step-end infinite alternate;}
}
