css:
@-moz-document domain("twitter.com") {
.follow-button {
    padding: 0px 4px 5px 3px !important;
    font-size: 12px;
}
.edit-button {
    padding: 5px 10px !important;
    font-size: 12px;
}
.user-dropdown {
    height: 26px !important;
}
.ProfileNav-label, .ProfileNav-value {
    font-size: 10px !important;
}
.ProfileCardMini {
    padding: 3px 15px !important;
    height: 47px !important;
}
.ProfileCanopy.is-locked .ProfileCanopy-card {
    overflow: hidden !important;
    height: 40px !important;
}
.ProfileCanopy-navBar {
    height: 45px !important;
}
.ProfileNav-stat {
    padding: 9px 15px 0px !important;
    height: 41px !important;
}
.ProfileNav-item--more .dropdown-menu {
    margin-top: 15px !important;
}
.ProfileTweet--high .ProfileTweet-text, .ProfileTweet--medium .ProfileTweet-text, .ProfileTweet--low .ProfileTweet-text {
    font-weight: 300 !important;
    font-size: 16px !important;
}
.dropdown-menu {
    padding: 5px 0 !important;
}
.ProfileNav-item.is-active .ProfileNav-stat, .ProfileNav-stat:hover, .ProfileNav-stat:focus {
    height: 44px !important;
}
.ProfileNav-item--userActions {
    padding: 10px 12px 0 2px !important;
    height: 40px !important;
}
}
