@-moz-document domain("twitch.tv") {

/* This brings back the emoticon-selector-toggle button and re-locates the image to be reliable */

a.emoticon-selector-toggle {
  	background: url("http://www-cdn.jtvnw.net/images/xarth/emoticon_selector_smile.png") no-repeat !important;
	width: 18px !important;
	height: 18px !important;
	display: block !important;
	position: absolute !important;
	top: 5px !important;
	right: 5px !important;
}
}
