css:
@-moz-document domain("androidgamehacks.net") {
html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form

{
color: #BBBBBB !important;
background: black !important;
}

div, a

{
background-color: black !important;
}

input, span, code, pre, div

{
color: #BBBBBB !important;
}


/*links*/

a

{
color: #61A12E !important;
}

a:visited

{
color: #286C09 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}
}
