css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("superpoint.ru") {
body{background:rgba(10,10,10, 0.9);
color: #ccc;
font-size: 1em;
}
#wrapper{background:rgba(13,13,13, 0.8);
}
a{color: rgba(0, 183, 9, 1);}
a:hover{color: rgba(5, 228, 0, 1); text-shadow:0px 0px 13px rgba(0, 183, 9, 1);;}
a.rollover:nth-child(9){background:url(http://fc07.deviantart.net/fs71/f/2014/102/0/5/select5a55_by_danillius-d7e56j6.png)}
textarea{color:#ccc; font-size: 1.5em; background-color:#000; }
.paginator span, .paginator a {font-size: 1.5em;}
div.menuitem, div.submenuitem{color: rgba(231, 0, 255, 1);}
#panel{background-color:#000;}
#content{background-color:#000;}
.pics_cell, .smiles_cell, .news_cell{background-color:#000;}
}
