css:
@-moz-document domain("www.phoronix.com") {
    body {
        font-family: serif !important;
    }
    div.phxcms_home_page_sub {
        margin-bottom: 25px;        
    }
    div#phxcms_toplogo {
        background: linear-gradient(to bottom, rgba(0, 0, 0, 1) 0%, rgba(74, 74, 74, 1) 100%) repeat scroll 0% 0% transparent;
    }
    td#phxcms_content_phx a, td#phxcms_content_phx a:active, td#phxcms_content_phx a:visited {
        color: black;
    }
    td#phxcms_content_phx h2 a, td#phxcms_content_phx h1 a {
        color: black;
    }
    td#phxcms_content_phx h2 a.phx_alt {
       color: rgba(0, 65, 198, 1);
    }
    td#phxcms_content_phx div.phxcms_page_shout a {
       font-size: 80%;
       color: rgba(0, 0, 33, 1);
    }
    div#phxcms_copyright {
       background: linear-gradient(to bottom, rgba(72, 72, 72, 1) 0%, rgba(0, 0, 0, 0.85) 100%) repeat scroll 0% 0% transparent;
    }
    td#phxcms_content_phx table tr td:first-child a {
        color: rgba(0, 5, 101, 1);
    }
}
