css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://app.simplenote.com/") {
    /*txtarea*/
    #txtarea {
        font-size:1em !important;
    }
    
    /*list*/
    .with-1-preview-lines {
        border-bottom:1px dashed #ccc;
    }
    
    /*markdown result*/
    #static_content {
        font-size:1em !important;
    }

    #static_content h3,
    #static_content h4,
    #static_content h5,
    #static_content h6 {
        font-weight:bold !important;
    }
    #static_content h1 {
        font-size:2em !important;
        text-align:left !important;
    }
    #static_content h2 {
        font-size:1.5em !important;
    }
    #static_content h3 {
        font-size:1em !important;
    }
    #static_content h4 {
        font-size:1em !important;
    }
    #static_content h5 {
        font-size:1em !important;
    }
    #static_content h6 {
        font-size:1em !important;
    }
    #static_content a {
        color:#333 !important;
        border-bottom:1px dashed #999;
    }
    #static_content a:hover {
        text-decoration:none !important;
        border-bottom:1px dashed #999 !important;
    }
    /*show links*/
    #static_content a::after {
        display:inline-block;
        content:"("attr(href)")";
        background-color:#448ac9;
        color:white;
        padding:1px 3px;
        font-size:0.8em;
        line-height:0.8em;
        border-radius:5px;
        margin:1px 0 0 2px;
    }
/*    #static_content a:hover::after {
        background-color:#559bea;
    }*/
    
    /*markdown result - list */
    #static_content ul {
        list-style:decimal !important;
    }
    #static_content ul > li > ul {
        list-style:decimal-leading-zero !important;
    }
    #static_content ul > li > ul > li > ul {
        list-style:upper-latin !important;
    }
    #static_content ul > li > ul > li > ul > li > ul {
        list-style:upper-roman !important;
    }
    #static_content ul > li > ul > li > ul > li > ul > li > ul {
        list-style:decimal-leading-zero !important;
    }
    
    
    .note {
        left:371px;
    }
    #static_content {
        width:60% !important;
        float:right !important;
        padding:20px 20px 20px 30px !important;
/*        background-color:#fbfbfb;*/
    }
    #txtarea {
        
    }
    #txtarea[style*="none"] {
        display:inline-block !important;
        width:40% !important;
    }
}
