css:
@-moz-document domain("net.jogtar.hu"), domain("www.complex.hu") {
header,nav,iframe,.banner,.breadcrumb-wrap,.menu-spaceholder,.banner-spaceholder,.bannercontainer,.banner-right{display: none !important}
#main{padding-top:0 !important}
.banner{display: none; padding:0}
#backtomain{margin:0}
a.button:hover{background: blue; border:1px solid green}
td{text-align:left !important;vertical-align:top;padding-right:4px}
.breadcrumb-spaceholder::before{content:"Az életünk olyan, mint egy nyári ruha mellénye: rövid és céltalan.";padding-top:20px;padding-left:100px;font-size:2em;color:darkred;text-shadow:1px 1px 2px grey}
#main:before{box-shadow: none;}
body.space-header.space-breadcrumb,body.space-header.space-banner,body.space-header {
    padding-top: 0 !important;
}
}
