css:
@-moz-document url-prefix("https://youraccount.atlassian.net/secure/") {
.ghx-issue
{
  font-size: 12px;
}

.ghx-issue-subtask
{
  margin-left: 30px;
  background-color: #f2f2f2;
}

.ghx-issue-subtask .ghx-type
{
  display: none;
}


.ghx-issue-subtask
{
  padding-left: 10px;
  padding-right: 5px;
  max-height: 45px;
}

.ghx-issue-subtask .ghx-priority
{
  display: none;
}

.ghx-issue-subtask .ghx-grabber
{
  max-height: 45px;
  width: 2px;
}

.ghx-issue-subtask .ghx-summary span.ghx-inner
{
  display: block;
  max-height: 15px;
  
}

.ghx-issue-subtask .ghx-avatar img
{
  width: 20px;
  height: 20px;
}
}
