background: url('http://i.imgur.com/n7tlT5j.png') center no-repeat !important;



background-color:#fff !important;



}



img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{



    width:0;



    height:70px;



    padding-right:250px;



    opacity: 0;



