css:
@-moz-document domain("play.baidu.com") {

[class*="top-banner"] {background:none !important;} /*顶部图片+链接广告*/

.gold-vip-tip /*顶部“了解白金音乐盒”按钮广告*/,
[name*="iframeName"] /*左下白金VIP广告*/,
.down-mobile /*上方“白金VIP”文字链接广告*/,
.mb-layout-bd.column4 /*右侧广告*/,
#rightAdTip /*点关闭按钮弹出的“开通白金VIP”广告*/,

.aAd-bg,.ui-widget-overlay /*全屏暗色限制*/,
[class*="mbox-audio"] /*歌曲结束跳出-暗色推广盒子（音频需ABP屏蔽）*/,
#pauseAd,IMG[src*="bs.baidu.com/music-xiaoyuan"] /*歌曲结束跳出-图片链接广告*/

{display:none !important;} 

/*-----------------------------*/

.column3{right:0px !important;}
.column2{margin-right:-165px !important;}
.lossless {margin-left:-240px !important;} /*“无损”标识*/

}
