        display: none;
    }
    .entry-content span {
        display: block;
        margin-right: auto;
        margin-left: auto;
        width: 320px;
        height: 480px;
        background: url(http://upload.wikimedia.org/wikipedia/commons/thumb/2/2f/Amritsar%2C_Golden_Temple_%286292343316%29.jpg/320px-Amritsar%2C_Golden_Temple_%286292343316%29.jpg) 0 0 no-repeat;
    }
    .entry-content blockquote p b {
        display: none;
    }
    .entry-content blockquote p {
        font-size: 3em;
    }
    .entry-content blockquote p:after {
        content: "HADIPPA!";
    }
}
