css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("https?://[\w\.]+\.baidu\.com/((s|baidu)\?|#wd|(index.*)?).*"){
/*布局拉宽*/
div#content_left, div#content_left>div{width:auto!important;float:none!important;}
div#content_left>table{width:100%!important;}
div.c-span18:not(.op-movievideo-right){width:auto!important;float:none!important;}
/*外框美化*/
div#content_left{border: 1px solid #ccc;padding:0!important;margin:0 35px!important;}
div#container{width:100%!important;}

/*奇数行美化*/
div#content_left>:nth-child(even){background-color: #EAEBEC!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*偶数行美化*/
div#content_left>:nth-child(odd){background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*搜索结果表格美化*/
div.c-border, div.op_best_answer_content, td.plf, td.op_wiseapp_f{width: auto!important;}
table.result, table.result-op{width: 100%!important;}
td.f, td.op_wiseapp_f{padding: 8px 10px!important;} 
table.subLink_factory_right{width: 50%!important;}

/*搜索结果最新信息美化*/
div.c-offset>div.c-row>span[style*="color:#666"]:first-child{color:#fff!important;float:left!important;Background:#2191f7!important;padding:0 5px!important;}

/*搜索结果优化*/
div.op_baikeperson_container>:first-child{width:auto!important;}

/*顶部为您推荐*/
div.hint_toprq,span.hint_toprq_items,span.hint_toprq_short,p.hit_top{width:auto!important}

/*搜索结果悬停美化*/
div#content_left>:hover{background-color: #B5E0F5!important;}

}
