css:
@-moz-document domain("podricing.org") { 

body > header {
    box-shadow: none !important;
    background: #000 !important;
    color: #fff !important;
    border-bottom: none !important;
}
.branding-header-logo {
    background-position: 0px -1936px !important;
    background: url('http://a.pomf.se/favmft.png') !important;
}
html, body, div, span, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, code, del, dfn, em, img, q, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, dialog, figure, footer, header, hgroup, nav, section {
    font-family: 'courier new', monospace, consolas !important;
}
#global_search form input:hover {
    background-color: #101010 !important;
}
#global_search form input {
    border-radius: 0px !important;
    background-color: #202020 !important;
    border: none !important;
    font-family: 'courier new', monospace, consolas !important;
}
ul#user_menu {
    border-right: none !important;
    border-left: none !important;
}
#nav_badges #notification_dropdown {
    background: #000 !important;
    float: left;
    color: #aaa !important;
}
}
