css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("debian.org") {

body, html, hr, h1, h2, h3, h4, h5, h6,
th, tr, td, table, p, li {
    background: none !important;
    background-color: #111 !important;
    color: #999 !important;
    font-family: sans !important;
    border: none !important;
    border-radius: 0.5em !important;
}

div, span {
    background-color: #111 !important;
    color: #999 !important;
    font-family: sans !important;
    border: none !important;
    border-radius: 0.5em !important;
}

img {opacity: .7 !important; transition: opacity .2s; border: none !important;}
img:hover, a:hover img {opacity: 1 !important}

a {
   color: #966 !important;
   background: none !important;
   border: none !important
}

pre {
    background: none !important;
    background-color: #522 !important;
    color: #999 !important;
    font-family: mono !important;
    font-size: 11pt !important;
    border: none !important;
    border-radius: 0.5em !important;
}

em {
    background: none !important;
    color: #797 !important;
}

}
