css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ubuntuforums.org") {

    html, body, code, pre {
        background: none !important;
        background-color: #000009 !important;
        border: none !important;
        color: #999 !important;
        text-shadow: none !important;
    }
    
    .topbar .topbar-links .search-container input[type="text"]  {
        background: none !important;
        background-color: #000009 !important;
        color: #999 !important;
    }
    
    div, span, p, h1, h2, 
    h3, h4, h5, table, tr, td, th,
    ul, li, input, select, 
    footer, header, nav {
        background: none !important;
        background-color: #081016 !important;
        border: none !important;
        color: #778 !important;
        box-shadow: none !important;
        text-shadow: none !important;
    }

    hr {   
        background-color: #000009 !important;
    }
    
    font {
        color: #d14413
    }
    
    blockquote {
        border: none !important;
        color: #778 !important;
    }
    
    .postbody, .postrow, .postbit, .postbitlegacy, 
    .postcontent, .signature.restore, .signaturecontainer, 
    .posttitle, .eventbit, #post_messsage, .userinfo, .userinfo_extra,
    .contact, .username_container, .rank, .userinfo_noavatar, .posthead,
    .date, .postdate, .nodecontrols, .textcontrols, .postcontrols, .imlinks,
    .postbitim, .postlinking
    {
        background-color: #000 !important;
        border-radius: 0.5em !important;
        padding: 2pt;
    }

   li, .postbit.postim.postcontainer.old, .eventbit {
        border-color: #d14413 !important;
        border: 5pt !important;
        margin-bottom: 10px !important;
        background-color: #000 !important;
    }

    .arrow-up {
        background: transparent !important;
    }

    a {
        border: none !important;
        color: #D14413 !important;
        background: none !important;
    }
    
    img {
        opacity: .7 !important;
        transition: opacity .2s;
        border-radius: 0.5em !important;
    }
    img:hover, a:hover img {
        opacity: 1 !important;
        border-radius: 0.5em !important;
    }
}
