css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.survivia.net") {
    

    body {
        background : url(http://image.noelshack.com/fichiers/2014/17/1398199037-73764520131012163121.png);
        background-attachment : fixed;
    }
    #branding {
        background: url(http://image.noelshack.com/fichiers/2014/17/1398200029-branding-bg.png);
    }
    #branding_right {
        background: url(http://image.noelshack.com/fichiers/2014/17/1398200029-branding-bg.png);
    }
    #branding_left {
        background: url(http://image.noelshack.com/fichiers/2014/17/1398200029-branding-bg.png);
    }
    .maintitle {
    background: url('http://image.noelshack.com/fichiers/2014/17/1398199346-maintitle.png') no-repeat scroll left bottom #313131;
    padding: 8px;
    border: 2px solid #313131;
    text-shadow: 0px 2px 2px black;
    border-radius: 4px 4px 0px 0px;
        
}
    .ipsBox {
         margin-bottom: 15px;
    }
    .ipsSideBlock h3 {
        background: #313131;
        color: white;
        text-shadow: 0px 2px 2px black;
        background-repeat: no-repeat;
        
    }
    a {
        color: #434343;
    }
    .unread {
        background: #F5F6F6;
    }
   #community_app_menu > li.active > a  {
        background: #760000;
        
    }
    #header_bar.clearfix {
        background: url(http://image.noelshack.com/fichiers/2014/17/1398353181-blanc.png);
    }
    .ipsList_inline.right {
        margin-left: 35%;
        float: left;
    }
    #user_navigation a {
    color: #313131;
    text-shadow: 0px 1px 1px gray;
    text-transform: uppercase;
    font-size: 14px;
}
}
