css:
@-moz-document domain("twitter.com") {
.ProfileCanopy--large, .ProfileCanopy--large .ProfileCanopy-header {
	height: 140px !important;
} 
  
.ProfileCanopy.is-locked .ProfileCanopy-header {
	margin-top: -95px !important;
}
  
.ProfileTweet--high .ProfileTweet-text, .ProfileTweet-text {
	font-size: 14px !important;
    line-height: 20px !important;
}

.ProfileHeading-toggleItem, .ProfileHeading-title {
    font-size: 16px !important;
}

body.ms-windows {
	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif !important;
}

.stream-container .tweet.gotham .tweet-text {
	font-size: 14px !important;
	line-height: 18px !important;
  	text-align: justify !important;
}

.stream-container .tweet.horizontal {
	font-family: inherit !important;
}
}
