css:
@-moz-document regexp('http.*://xkcd.com.*') {
    body {
        background-color: #1c1c1c ! important;
        color: #afd787 ! important;
    }
    
    .box {
        background-color: #1c1c1c ! important;
        border-radius: 0px ! important;
        border-color: #afd787 ! important;
    }
    
    #topLeft a {
        color: #afd787 ! important;
    }
        
    #bottom { 
        display: none ! important;
    }
    
    ul.comicNav li a { 
        background-color: #1c1c1c ! important;
        border-color: #afd787 ! important;
        border-radius: 0px ! important;
        box-shadow: none ! important;
        color: #afd787 ! important;
    }
    
    ul.comicNav li:hover {
        opacity: 1.0;
        transition: opacity .1s cubic-bezier(0.0,0.0,0.580,1.0);
    }
    
    ul.comicNav li {
        opacity: .5;
        transition: opacity .5s cubic-bezier(0.420,0.0,1.0,1.0);
    }
    
    ul.comicNav li:hover {
        opacity: 1.0;
        }
    
    ul.comicNav li a:hover {
        border-color: #d6ce87!important;
        color: #d6ce87!important;
    }
    
    #topContainer { 
        display: none ! important;
    }

    img[alt="xkcd.com logo"] { 
        display: none ! important;
        }
        
    #slogan { 
        border-left: 1px solid #d6d656;
        color: #d6d656 ! important;
        margin: 0 0 10px;
    }

}
