@-moz-document domain("twitter.com") {


    body {
    background-image: url("http://2.bp.blogspot.com/-hvPk5sWRq8g/U10PpOU3pVI/AAAAAAAABsI/Vk5ayxOoEzQ/s1600/Miku+twett.jpg") !important;
    }
       
    DIV.front-welcome {
    visibility:hidden !important
    }
    
    .global-nav {background:rgba(79, 83, 92, 1) !important;}
    

    

DIV.front-signin.js-front-signin {
float: right;
  position: relative;
  border: 0;
  border-radius: 0!important;
  margin: 1px 0px 2px 0!important;
  border-color: #76797F !important;
    }
    
DIV.front-signup.js-front-signup {
float: right;
  position: relative;
  border: 0;
  border-radius: 0!important;
  margin: 1px 0px 2px 0!important;
  border-color: #76797F !important;
    }
    
BUTTON.btn.signup-btn {
    border-color: #76797F !important;
    }
    
    .dashboard *,.content-main *, .topbar *, .expansion-container *,.content-main, .content-narrow, .page-canvas, .permalink, .profile-card.profile-header .avatar,
.module ul:first-child li:first-child .list-link, .module .flex-module:first-child, .module .contrast-flex-module:first-child,
.module ul:last-child li:last-child .list-link, .module ul:last-child li.last-item .list-link, .module .list-link.last, 
.module .flex-module:last-child, .module .contrast-flex-module:last-child, .btn, .modal-header, .modal-content,
