css:
@-moz-document domain("clubhousehub.co.uk") {
/* I've made everything !important because sometimes it wasn't being applied in the right order... suggestions welcome */

#topmenu ul li, #topmenu ul li:first-of-type {
    width: auto !important;
    padding: 0px !important;
    margin: 15px 0px 0px 0px !important;
}

#topmenu ul li:first-of-type {
    padding-left: 0px !important;
    border-left: none !important;
}

#topmenu ul li a {
    width: auto !important;
    padding: 0px 10px !important;
}

#topmenu ul li:first-of-type a {
    padding-left: 0px !important;
}

table {
    border-collapse: collapse !important;
}

table.querygrid th>span, table.querygrid td>span {
    border-bottom: none !important;
}
table.querygrid td {
    border-bottom: solid 1px #EEE !important;
}

#calendar td {
    border: 1px solid #87A162 !important;
}
#calendar span.day-number {
    border: none !important;
}
#calendar .calendar-end {
    display: none !important;
}
#calendar span.day-number {
    border-top: none !important;
}

.pager a {
    background-color: #fff !important;
    margin: 0px !important;
}
.pager a:nth-child(n+11) {
    display: none !important;
}
.pager a:hover {
    color: inherit !important;
    background-color: #E9F289 !important;
}
    
td[title="click for athlete details"] span:hover, td[title="click for club details"] span:hover {
   text-decoration: underline !important;
}
}
