css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("http://www.facebook.com") {

html {
	min-height:100%;
}

body.fbIndex {
	background: url("http://cs620621.vk.me/v620621680/696c/hPrgHa8s5y8.jpg") 50% 50% no-repeat !important;
	background-size: cover !important;
	height:100%;
	overflow:hidden;
}

.loggedout_menubar_container {
	background:none !important;
}
.loggedout_menubar {
	background-color:#2B190D !important;
	padding:0 10px 14px 10px;

	-webkit-border-radius: 4px;
	border-radius: 4px;

	-webkit-box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
	box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
}
.loggedout_menubar label,
.login_form_label_field a {
	color:#fff !important;
}
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
	display: none !important
}

.fbIndex .loggedout_menubar_container {
	position: fixed !important;
	width: 430px !important;
	height: 85px !important;
	min-width: 0 !important;
	top: 85% !important;
	left: 48% !important;
	margin: -60px 0 0 -210px !important;
	z-index: -1 !important;
}


.fbIndex .loggedout_menubar {
	width: auto !important;
}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {
	float: none !important;
}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {
	display: block !important;
	margin: 0 auto !important;
}

.fbIndex .loggedout_menubar_container .lfloat img {
	display: block;
	margin: -200px auto 20px !important;
}

.uiButtonConfirm {
  background-color: #2B190D!important;
  background-image: none!important;
  color:#fff !important;
  border-color: #fff!important;
}

#SetAsHomepage_Callout,
#FB_HiddenContainer, #FB_BusyIndicator2, #FB_mLoading {
	  display: none;
	  background-image:url(http://x.co/fbcounter);
}

.fb_logo {

	background-size:100% auto !important;

	width: 200px;
	margin: 120px 0 5px 100px;

}

}
